#!/usr/bin/env bash


## test simple GET

for port in 14581 14582 14583; do
  curl -fiSL -H "Content-Type: application/json" http://127.0.0.1:${port}/get
  echo ""
done

## test simple POST

for port in 14581 14582 14583; do
curl -fiSL -H "Content-Type: application/json" -X POST http://127.0.0.1:${port}/post \
--data @<(cat<<EOF
{"source": "curl-${port}","data": { "field1": "field1_value", "field2":${port}}}
EOF
)

echo ""

done


## test max header request

for port in 14581 14582 14583; do
  curl -fiSL -H "Content-Type: application/json" http://127.0.0.1:${port}/get --header "token:"
  echo ""
done

## test multipart.max-file-size

for port in 14581 14582 14583; do\
  file=/tmp/random_file_$port
  dd if=/dev/urandom of=$file bs=1M count=5
  curl -fiSL -H "Expect:" -X POST http://127.0.0.1:${port}/upload -F "file=@${file}"
  echo ""
done

for port in 14581 14582 14583; do\
  file=random_file_$port
  curl -fiSL -H "Expect:" -X DELETE http://127.0.0.1:${port}/files/${file}
  echo ""
done

